import React from 'react';
import './CandidateItem.css';
import profile from '../../images/user.png';
import { Link, withRouter } from 'react-router-dom';
import { useSelector } from 'react-redux';

const CandidatesItems = (props) => {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  return (
    <div>
      {userInfo.id !== props.user ? (
        <div>
          {props.isPass && (
            <div className='posts'>
              <Link to='/post'>
                <div className='description'>
                  <div>
                    <img src={profile} alt={props.name} />
                    <span style={{ marginLeft: '20px', fontSize: '12px' }}>
                      <span style={{ color: 'green' }}>Name:</span>
                      {props.name}
                    </span>
                    <h4 style={{ fontSize: '12px' }}>
                      <spn style={{ color: '#000' }}>Applied For: </spn>
                      <sp>{props.title}</sp>
                    </h4>
                    <h4 style={{ fontSize: '12px' }}>
                      <span style={{ color: '#000' }}>E-mail:</span>{' '}
                      {props.candidate_email}
                    </h4>
                    <p
                      style={{
                        fontSize: '15px',
                        width: '90%',
                        marginLeft: '10px',
                        fontFamily: 'sans-serif',
                      }}>
                      <span style={{ marginRight: '10px', color: 'seagreen' }}>
                        Bio:
                      </span>
                      {props.bio.slice(1, 100)}
                    </p>
                    <Link to={`/profile/candidates/${props.id}`}>
                      <button>proceed to vote</button>
                    </Link>
                  </div>
                </div>
              </Link>
            </div>
          )}
        </div>
      ) : (
        ''
      )}
    </div>
  );
};

export default withRouter(CandidatesItems);
