import React from 'react';
import './CandidateItem.css'
import {Link} from "react-router-dom";
const CandidatesItems = (props) => {
    const {candidates} = props

  return (
    <div className='posts'>
      <Link to='/post'>
        <div className='description'>
          <img src={props.image} alt='' />
          <h4 >{props.title}</h4>
          <h6>
            {' '}
            {props.numCandidates === 1 ? 'Application' : 'Applications'} :
            <span
              style={{ color: props.numCandidates === 10 ? '#fcc102' : 'green' }}>
              {props.numCandidates}
            </span>
          </h6>
          <h6>
            {candidates.description.slice(0,20)}

          </h6>

          <Link to='/profile'>
            <button>View Profile</button>
          </Link>

        </div>
      </Link>
    </div>
  )
};

export default CandidatesItems;
