import React,{useEffect,useState} from 'react'
import './PostScreen.css'
import {useSelector,useDispatch} from 'react-redux'
import { listPostDetails } from '../Action/PostActions';
// import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import Card from '../shared/components/UIElements/Card'
import Avatar from '../shared/components/UIElements/Avatar'
import {Link} from 'react-router-dom'


function PostScreen({match}) {
    const dispatch = useDispatch();


  const postDetails = useSelector((state) => state.postDetails);
  const { post, loading, errors } = postDetails;
  const [candidateState, setCandidateState] = useState([])
    console.log(candidateState)

    const candidates =[
        {
            "id": 3,
            "name": "Makanya",
            "title": "President",
            "question": "Dolor",
            "answer": "Vivamus sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.",
            "numCasts": 0,
            "isPass": false,
            "post": 3,
            "user": 4
        }
    ]

  useEffect(() => {
    dispatch(listPostDetails(match.params.id));
    setCandidateState(post.candidate)
  }, [dispatch, match]);
    return (
        <div className="main-post-details">
        <div className="section-image">
       <img src={post.image} alt={post.title}/>
        </div>
        <div class="section-description">
        <h1>{post.title}</h1>
        </div>
        <div class="section-candidate">
         {candidates.map((candidate)=>(

            <div className='posts'>
            <Link to='/post'>
              <div className='description'>
                <div key={candidate.id}>
                <img src={candidate.image} alt={candidate.name} />
                 <h4 >{candidate.name}</h4>
                 <h4 >{candidate.title}</h4>
                <Link to='/profile'>
                  <button>View Profile</button>
                </Link>
             </div>


              </div>
            </Link>
          </div>
         ))}
        </div>


      </div>
    )
}

export default PostScreen
