import React, { useEffect, useState } from 'react';
import Input from '../shared/components/FormElements/Input';
import Button from '../shared/components/FormElements/Button';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE,
} from '../shared/util/validators';
import { useForm } from '../shared/hooks/form-hook';
import { RegisterUser,getUserDetails } from '../Action/UserActions';

function EditProfileScreen({ location, history }) {
  const dispatch = useDispatch()

  const useDetails = useSelector(state => state.useDetails)
  const {loading,error,user} = useDetails

  const userLogin = useSelector(state => state.userLogin)
  const {userInfo} = userLogin

useEffect(() => {
  if(!userInfo){
history.push('/login')
  }else{
    if(!user || !user.name){
      dispatch(getUserDetails('profile'))
    }
  }

}, [dispatch,userInfo,user,history])

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  const [formState, inputHandler] = useForm(
    {
      email: {
        value: '',
        isValid: false,
      },
      password: {
        value: '',
        isValid: false,
      },
      passwordConfirm: {
        value: '',
        isValid: false,
      },
      name: {
        value: '',
        isValid: false,
      },

      location: {
        value: '',
        isValid: false,
      },

      image: {
        value: '',
        isValid: false,
      },

      bio: {
        value: '',
        isValid: false,
      },
    },
    false
  );
  const registerSubmitHandler = (e) => {
    e.preventDefault();
    if (
      formState.inputs.password.value !== formState.inputs.passwordConfirm.value
    ) {
      setMessage('Password do not match');
    } else {
      dispatch(
        RegisterUser(
          formState.inputs.name.value,
          formState.inputs.email.value,
          formState.inputs.password.value,
          formState.inputs.bio.value,
          formState.inputs.location.value,
          formState.inputs.image.value,
          formState.inputs.passwordConfirm.value
        )
      );
    }
  };
  return (
    <form onSubmit={registerSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>
          <div className='form-all'>
            <h2>EDIT YOUR PROFILE  </h2>
            {message && <Card className='error_message'>{message}</Card>}
            {error && <Card className='error_message'>{error}</Card>}
            {loading && <LoadingSpinner asOverlay />}
            <Input
              element='input'
              id='name'
              type='text'
              label='Name'
              placeholder='Enter Name'
              validators={[VALIDATOR_REQUIRE()]}
              errorText='Name Required'
              onInput={inputHandler}
            />
            <Input
              element='input'
              id='email'
              type='email'
              label='E-Mail'
              placeholder='Enter E-Mail'
              validators={[VALIDATOR_EMAIL()]}
              errorText='Please enter a valid email address.'
              onInput={inputHandler}
            />

            <Input
            element='input'
            id='location'
            type='text'
            label='Address(Country/City)'
            placeholder='Enter Address'
            errorText='Address Required'
            validators={[VALIDATOR_REQUIRE()]}
            onInput={inputHandler}
          />
          <Input
          element=''
          id='bio'
          type='text'
          label='Bio'
          placeholder='Enter Bio'
          validators={[VALIDATOR_REQUIRE()]}
          errorText='Bio Required'
          onInput={inputHandler}
        />
        <Input
        element='input'
        id='image'
        type='file'
        label='File'
        placeholder='choose profile image'
        validators={[VALIDATOR_REQUIRE()]}
        errorText='Invalid file'
        onInput={inputHandler}
      />
            <Input
              element='input'
              id='password'
              type='password'
              label='Password'
              placeholder='Enter Password'
              validators={[VALIDATOR_MINLENGTH(6)]}
              errorText='Please enter a valid password, at least 6 characters.'
              onInput={inputHandler}
            />
            <Input
              element='input'
              id='passwordConfirm'
              type='password'
              label='Confirm Password'
              placeholder='Enter Password again'
              validators={[VALIDATOR_MINLENGTH(6)]}
              errorText="Password don't match"
              onInput={inputHandler}
            />
            <Button type='submit' disabled={!formState.isValid}>
              SAVE PROFILE
            </Button>




          </div>
        </div>
      </div>
    </form>
  );
}



export default EditProfileScreen;
