import { Link,NavLink,withRouter } from 'react-router-dom';
import {useDispatch,useSelector} from 'react-redux'
import './PostItem.css';
import React,{useEffect} from "react";
import { CandidateCreate } from '../../Action/CandidateActions';
import { APPLY_CREATE_RESET } from '../../constants/CandidatesConstants';

const PostItem = (props) => {
const {id,title,applications,description,image,history}  = props
const dispatch = useDispatch()

const createApplication = useSelector(state => state.createApplication)
const {success,loading,error} = createApplication

useEffect(() => {
 if(success){
   dispatch({type:APPLY_CREATE_RESET})
   history.push(`/apply/${id}`)
 }
}, [dispatch,success,history,id])

  const createCandidateHandler = () =>{
    dispatch(CandidateCreate(id))

  }
  return (
    <div>
    {props.active?(
      <div className='posts'>

        <div className='description'>
          <img src={image} alt={title} />
          <h4 >{title}</h4>
          <h6>
            {' '}
            {applications === 1 ? 'Application' : 'Applications'} :
            <span
              style={{ color: applications === 10 ? '#fcc102' : 'green' }}>
              {applications}
            </span>
          </h6>
          <h6>
            Post:
            {
              <span
                style={{ color: applications === 10 ? 'red' : '#fcc102' }}>
                {applications === 10 ? 'Closed' : 'open'}
              </span>
            }
          </h6>
          <p>{description.slice(0,60)}</p>
          <Link to={`/post/${id}`}> <button>More info</button></Link>

         <button onClick={createCandidateHandler} className='apply'>Apply</button>


        </div>

    </div>
    ):''}
    </div>

  );
};

export default withRouter(PostItem);
