import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import {Link} from 'react-router-dom'
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import './EditProfileScreen.css'
import Notification from '../shared/components/UIElements/Notification'
import { listPostDetails, PostUpdate } from '../Action/PostActions';
import { APPLY_CREATE_RESET } from '../constants/CandidatesConstants';
import { getCandidateDetails } from '../Action/CandidateActions';

function ApplyScreen({ match, history }) {
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });
 const candidateId = match.params.id
  const [title, setTitle] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address,setAddress] = useState('');
  const [applyReason,setApplyReason] = useState('');
  const [description, setDescription] = useState('');



  const dispatch = useDispatch()

  const candidateDetails = useSelector(state => state.candidateDetails)
  const {loading,error,candidate} = candidateDetails


  // const postUpdate = useSelector(state => state.postUpdate)
  // const {success:successUpdate,error:errorUpdate,loading:loadingUpdate} = postUpdate

  useEffect(() => {
    // if (successUpdate) {
    //   dispatch({type:APPLY_CREATE_RESET})
    //   history.push('/admin/posts')

    // }else{
      if(!candidate.name || candidate.post !== Number(candidateId)){
        dispatch({type:APPLY_CREATE_RESET})
        dispatch(getCandidateDetails(candidateId))
        }else{
          setTitle(candidate.title)
          setDescription(candidate.bio) 
          setName(candidate.name) 
          setEmail(candidate.candidate_email)
          setAddress(candidate.address)
          setApplyReason(candidate.whyApply)
        }


  }, [dispatch,candidate,candidateId])

  const updateSubmitHandler = (e) => {
    e.preventDefault();

   dispatch(PostUpdate({
    //  id:postId, title,description,isActive,isApplying
   }))
    setNotify({
      isOpen: true,
      message: 'Submitted Successfully',
      type: 'success',
    });

  };
  return (

    <div>
    <Link to="/admin/posts/">
    <button  style={{position:'absolute',left:'200px' ,outline:'none'}} type="button" className="btn btn-outline-secondary">GO BACK</button>
    </Link>
    <form onSubmit={updateSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>

        {loading? <LoadingSpinner/>:error?<Card>{error}</Card>:
        <div className='form-all'>
        <h2> Your applying for <spn style={{color:'seagreen',marginLeft:'3px',fontWeight:'600'}}>{candidate.title} </spn>  Post  </h2>

      <div className="form-control">
      <label htmlFor="title">Post Title</label>
      <input 
      type="text" 
      id="title" 
      placeholder="Enter Post Title" 
      value={title}
      onChange={(e) => setTitle(e.target.value)} />

    </div>

    <div className="form-control">
    <label htmlFor="title"> Name</label>
    <input 
    type="text" 
    id="title" 
    placeholder=" Applicant Name" 
    value={name}
    onChange={(e) => setName(e.target.value)} />

  </div>

  <div className="form-control">
  <label htmlFor="title"> Email</label>
  <input 
  type="email" 
  id="title" 
  placeholder=" Applicant Email" 
  value={email}
  onChange={(e) => setEmail(e.target.value)} />

</div>

<div className="form-control">
<label htmlFor="title"> Address</label>
<input 
type="email" 
id="title" 
placeholder=" Applicant address" 
value={address}
onChange={(e) => setAddress(e.target.value)} />
</div>

    <div className="form-control">
      <label htmlFor="description">Biography</label>
      <textarea type="text" 
      id="description" 
      placeholder="Write a Description"  
      value={description}
      onChange={(e) => setDescription(e.target.value)}/>
    </div>

    <div className="form-control">
      <label htmlFor="description">Why are you applying for this Post?</label>
      <textarea type="text" 
      id="description" 
      placeholder="why this post? "  
      value={applyReason}
      onChange={(e) => setApplyReason(e.target.value)}/>
    </div>



        <Button type='submit'>
          SAVE UPDATES
        </Button>




</div>
      }

        </div>
      </div>
    </form>
    <Notification notify={notify} setNotify={setNotify} />

    </div>

  );
}



export default ApplyScreen;
