import React,{useEffect,useState} from 'react'
import {useSelector,useDispatch} from 'react-redux'
import {Link} from 'react-router-dom'
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner'
import Card  from '../shared/components/UIElements/Card'
import './UserListScreen.css'
import { listUsers } from '../Action/UserActions'
import { EditOutlined, DeleteOutline } from '@material-ui/icons';
import Controls from '../shared/controls/Controls'
import Notification from '../shared/components/UIElements/Notification'
import ConfirmDialog from '../shared/components/UIElements/ConfirmDialog'
import Popup from '../shared/components/UIElements/Popup'
import EditUserScreen from './EditUserScreen'
import EmployeesForm from '../shared/components/UIElements/EmployeesForm'

function UserListScreen() {

    const [openPopup, setOpenPopup] = useState(false);
    const [confirmDialog, setConfirmDialog] = useState({
            isOpen: false,
            title: '',
            subTitle: '',
        });
    const [notify, setNotify] = useState({
            isOpen: false,
            message: '',
            type: '',
        });
    const openInPopup = () => {
        // setRecordForEdit(item);
        setOpenPopup(true);
        };
    const onDelete = (id) => {
        setConfirmDialog({
            ...confirmDialog,
              isOpen: false,
            });
        }    

    const dispatch = useDispatch()
    const userList = useSelector(state => state.userList)
    const {users,loading,error} = userList



useEffect(() => {
    dispatch(listUsers())
}, [dispatch])
    return (
        <React.Fragment>
        <div className="user__list">
        <h2>Users List</h2>

        {loading ?<LoadingSpinner asOverlay/>:error?<Card>{error}</Card>:
        <table>
        <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Role</th>
            <th>Candidate</th>
            <th>Actions</th>
        </tr>
        {users.map((user)=>(
            <tr key={user.id}>
            <td>{user.name}</td>
            <td>{user.email}</td>
            <td>{user.location}</td>
            <td>{user.isAdmin?'Yes':'No'}</td>
            <td>{user.isCandidate?'Yes':'No'}</td>
            <td>
            <Controls.ActionButton color='primary'>
            <EditOutlined
              fontsize='small'
              onClick={() => {
                openInPopup();
              }}
            />
          </Controls.ActionButton>

          <Controls.ActionButton
                    onClick={() => {
                      setConfirmDialog({
                        isOpen: true,
                        title: 'Are you sure to delete this record?',
                        subTitle: "You can't undo this operation!",
                        onConfirm: () => {
                          onDelete();
                        },
                      });
                      // onDelete(item.id);
                    }}
                    color='secondary'>
                    <DeleteOutline fontsize='small' />
                  </Controls.ActionButton>
            </td>
        </tr>
        ))}



        </table>





    }


        </div>

        <Popup
        openPopup={openPopup}
        setOpenPopup={setOpenPopup}
        title='Employee Form'>
        <EmployeesForm recordForEdit={''} addOrEdit={''} />
      </Popup>
      <Notification notify={notify} setNotify={setNotify} />
      <ConfirmDialog
        confirmDialog={confirmDialog}
        setConfirmDialog={setConfirmDialog}
      />
      </React.Fragment>
    )
}

export default UserListScreen
