import React, { useState, useEffect } from 'react';
import EmployeesForm from './EmployeesForm';
import LoadingSpinner from '../../../shared/components/UIElements/LoadingSpinner';
import Card from '../../../shared/components/UIElements/Card';
import Controls from '../../Components/controls/Controls';
import SearchIcon from '@material-ui/icons/Search';
import Popup from '../../Components/Popup';
import Notification from '../../Components/Notification';
import ConfirmDialog from '../../Components/ConfirmDialog';
import * as employeeServices from '../../services/EmployeesService';

import {
  Paper,
  makeStyles,
  TableBody,
  TableRow,
  TableCell,
  Toolbar,
  InputAdornment,
} from '@material-ui/core';
import useTable from '../../Components/useTable';
import * as employeeService from '../../services/EmployeesService';
import AddIcon from '@material-ui/icons/Add';
import { EditOutlined, DeleteOutline } from '@material-ui/icons';
import { useSelector, useDispatch } from 'react-redux';
import {
  deleteUser,
  getUserDetails,
  listUsers,
} from '../../../Action/UserActions';

const useStyles = makeStyles((theme) => ({
  pageContent: {
    margin: theme.spacing(5),
    padding: theme.spacing(3),
  },
  searchInput: {
    width: '75%',
  },
  newButton: {
    position: 'absolute',
    right: '10px',
  },
}));

const headCells = [
  { id: 'name', label: 'User Name' },
  { id: 'email', label: 'Email Address (Personal)' },
  { id: 'Admin', label: 'User Status', disableSorting: true },
  { id: 'Candidate', label: 'Candidate', disableSorting: true },
  { id: 'actions', label: 'Actions ', disableSorting: true },
];

const Employees = ({ history }) => {
  const dispatch = useDispatch();

  const userList = useSelector((state) => state.userList);
  const { loading, error, users } = userList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userDetails = useSelector((state) => state.userDetails);
  const { user, loading: userLoading, error: userError } = userDetails;

  const userDelete = useSelector((state) => state.userDelete);
  const { success: successDelete } = userDelete;

  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(listUsers());
    } else if (!user.name || user.id !== userInfo.id) {
      dispatch(getUserDetails(user.id));
    } else {
      history.push('/login');
    }
  }, [dispatch, userInfo, history, user, userDetails, successDelete]);

  const classes = useStyles();

  const [recordForEdit, setRecordForEdit] = useState(null);
  const [records, setRecords] = useState(users);
  const [filterFn, setFilterFn] = useState({
    fn: (items) => {
      return items;
    },
  });
  const [openPopup, setOpenPopup] = useState(false);
  const [confirmDialog, setConfirmDialog] = useState({
    isOpen: false,
    title: '',
    subTitle: '',
  });
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });

  const {
    TblPagination,
    TblContainer,
    TblHeader,
    recordsAfterPaginationSorting,
  } = useTable(records, headCells, filterFn);

  const handleSearch = (e) => {
    let target = e.target;
    setFilterFn({
      fn: (items) => {
        if (target.value === '') {
          return items;
        } else {
          return items.filter((x) => x.fullName.includes(target.value));
        }
      },
    });
  };
  const addOrEdit = (employee, resetForm) => {
    if (employee.id === 0) {
      employeeServices.insertEmployee(employee);
    } else {
      employeeServices.updateEmployee(employee);
      setRecordForEdit(null);
      resetForm();
      setOpenPopup(false);
      setRecords(employeeService.getAllEmployees());
      setNotify({
        isOpen: true,
        message: 'Submitted Successfully',
        type: 'success',
      });
    }
  };

  const openInPopup = (item) => {
    setRecordForEdit(item);
    setOpenPopup(true);
  };

  const onDelete = (id) => {
    setConfirmDialog({
      ...confirmDialog,
      isOpen: false,
    });
    dispatch(deleteUser(id));
    setRecords(users);
    setNotify({
      isOpen: true,
      message: 'Deleted Successfully',
      type: 'error',
    });
  };
  return (
    <>
      {loading ? (
        <LoadingSpinner asOverlay />
      ) : error ? (
        <Card>{error}</Card>
      ) : (
        <Paper className={classes.pageContent}>
          <Toolbar>
            <Controls.Input
              className={classes.searchInput}
              label='Search User'
              InputProps={{
                startAdornment: (
                  <InputAdornment position='start'>
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
              onChange={handleSearch}
            />
            <Controls.Button
              className={classes.newButton}
              text='Add New'
              variant='outlined'
              startIcon={<AddIcon />}
              onClick={() => setOpenPopup(true)}
            />
          </Toolbar>
          <TblContainer>
            <TblHeader />
            <TableBody>
              {recordsAfterPaginationSorting().map((item) => (
                <TableRow key={item.id}>
                  <TableCell>{item.name}</TableCell>
                  <TableCell>{item.email}</TableCell>
                  <TableCell>{item.isAdmin ? 'Yes' : 'No'}</TableCell>
                  <TableCell>{item.isCandidate ? 'Yes' : 'No'}</TableCell>
                  <TableCell>
                    <Controls.ActionButton color='primary'>
                      <EditOutlined
                        fontsize='small'
                        onClick={() => {
                          openInPopup(item);
                        }}
                      />
                    </Controls.ActionButton>
                    <Controls.ActionButton
                      onClick={() => {
                        setConfirmDialog({
                          isOpen: true,
                          title: 'Are you sure to delete this record?',
                          subTitle: "You can't undo this operation!",
                          onConfirm: () => {
                            onDelete(item.id);
                          },
                        });
                        // onDelete(item.id);
                      }}
                      color='secondary'>
                      <DeleteOutline fontsize='small' />
                    </Controls.ActionButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </TblContainer>
          <TblPagination />
        </Paper>
      )}
      <Popup
        openPopup={openPopup}
        setOpenPopup={setOpenPopup}
        title='User Form'>
        <EmployeesForm recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </Popup>
      <Notification notify={notify} setNotify={setNotify} />
      <ConfirmDialog
        confirmDialog={confirmDialog}
        setConfirmDialog={setConfirmDialog}
      />
    </>
  );
};

export default Employees;
