import React from 'react';
import './CandidateItem.css';
import { Link, withRouter } from 'react-router-dom';
const CandidatesItems = (props) => {
  return (
    <div>
      {props.isPass && (
        <div className='posts'>
          <Link to='/post'>
            <div className='description'>
              <div>
                <img src={props.candidate_image} alt={props.name} />
                <h6>
                  <span style={{ color: 'green' }}>Email: </span>
                  {props.name}
                </h6>
                <h4>
                  <spn>Applied For: </spn>
                  {props.applyingFor}
                </h4>
                <h4>Name: {props.name}</h4>
                <h4>E-mail: {props.candidate_email}</h4>
                <p>Bio:{props.bio.slice(1, 100)}</p>
                <Link to={`/users/${props.id}`}>
                  <button>View Profile</button>
                </Link>
              </div>
            </div>
          </Link>
        </div>
      )}
    </div>
  );
};

export default withRouter(CandidatesItems);
