import React, { useEffect, useState } from 'react';

import { Link, withRouter } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { listPostDetails } from '../Action/PostActions';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import Card from '../shared/components/UIElements/Card';
import { APPLY_CREATE_RESET } from '../constants/CandidatesConstants';
import Button from '../shared/components/FormElements/Button'
const VotingScreenPage = ({ match, history }) => {
  const dispatch = useDispatch();

  const [data, setData] = useState([]);
  const [data1, setData1] = useState();
  const [loadingData, setLoadingData] = useState(false);

  const postId = match.params.id;

  const postDetails = useSelector((state) => state.postDetails);
  const { post, loading, errors } = postDetails;
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const createApplication = useSelector((state) => state.createApplication);
  const {
    success: successCreated,
    loading: loadingCreated,
    error: errorCreated,
  } = createApplication;

  useEffect(() => {
    dispatch({ type: APPLY_CREATE_RESET });
    if (!userInfo) {
      history.push('/login/user');
    } else if (successCreated) {
      history.push(`/user/candidate/${post.id}/edit`);
    } else {
      dispatch(listPostDetails(postId));
      if (post.candidates) {
        setLoadingData(false);
        setData(post.candidates);
      } else {
        setData([]);
        setLoadingData(true);
      }
    }
  }, [dispatch, postId, history, post, successCreated, userInfo]);

  if (loadingData) {
    return <LoadingSpinner asOverlay />;
  }

  return (
    <form style={{ marginTop: '200px' }}>

        <h1>
          You are Voting for{' '}
          <span style={{ margin: '10px', color: 'seagreen' }}>
            {post.title}
          </span>
          Post
        </h1>


      <div className='section-candidate'>
        <Card>
          {data.map((postCandidate) => {
            return (
              <div>
                {postCandidate.isPass && (
                  <div className='posts'>
                    <div className='description'>
                      <div>
                        <img
                          src={postCandidate.candidate_image}
                          alt={postCandidate.name}
                        />
                        <span style={{ marginLeft: '20px', fontSize: '12px' }}>
                          <span style={{ color: 'green' }}>Name:</span>
                          {postCandidate.name}
                        </span>
                        <h4 style={{ fontSize: '12px' }}>
                          <spn style={{ color: '#000' }}>Applied For: </spn>
                          <sp>{postCandidate.title}</sp>
                        </h4>
                        <h4 style={{ fontSize: '12px' }}>
                          <span style={{ color: '#000' }}>E-mail:</span>{' '}
                          {postCandidate.candidate_email}
                        </h4>

                        <p
                          style={{
                            fontSize: '15px',
                            width: '90%',
                            marginLeft: '10px',
                            fontFamily: 'sans-serif',
                          }}>
                          <span
                            style={{
                              marginRight: '10px',
                              color: 'seagreen',
                            }}>
                            Bio:
                          </span>
                          {postCandidate.bio.slice(1, 100)}
                        </p>

                        <div class="form-check">
                  <input style={{padding:'20px'}} class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"/>
                  <label style={{fontSize:'18px'}} class="form-check-label" for="flexRadioDefault1">
                    {postCandidate.name}
                  </label>
                </div>
                                      </div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
          <Button type="submit">submit vote</Button>
        </Card>

      </div>
    </form>
  );
};

export default withRouter(VotingScreenPage);
