import { Link, NavLink, withRouter } from 'react-router-dom';
import './PostItem.css';
import React from 'react';

const PostItem = (props) => {
  const { id, title, applications, description, image } = props;

  // const candidate =post.candidates

  return (
    <div>
      {props.active ? (
        <div className='posts'>
          <NavLink to={``}>
            <div className='description'>
              <img src={image} alt={title} />
              <h4>{title}</h4>
              <h6>
                {' '}
                {applications === 1 ? 'Application' : 'Applications'} :
                <span
                  style={{ color: applications === 10 ? '#fcc102' : 'green' }}>
                  {applications}
                </span>
              </h6>
              <h6>
                Post:
                {
                  <span
                    style={{ color: applications === 10 ? 'red' : '#fcc102' }}>
                    {applications === 10 ? 'Closed' : 'open'}
                  </span>
                }
              </h6>
              <span
                style={{
                  marginRight: '10px',
                  color: 'seagreen',
                }}>
                Description:
              </span>
              <p>{description.slice(0, 60)}</p>
              <Link to={`/post/${id}`}>
                {' '}
                <button>More info</button>
              </Link>
            </div>
          </NavLink>
        </div>
      ) : (
        ''
      )}
    </div>
  );
};

export default withRouter(PostItem);
