import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import {Link} from 'react-router-dom'
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import { USER_UPDATE_PROFILE_RESET } from '../constants/UserConstant';
import './EditProfileScreen.css'
import { getUserDetails } from '../Action/UserActions';

function EditUserScreen({ match, history }) {
 const userId = match.params.id
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [bio, setBio] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [isCandidate, setIsCandidate] = useState(false);


  const dispatch = useDispatch()

  const useDetails = useSelector(state => state.useDetails)
  const {loading,error,user} = useDetails

  const userLogin = useSelector(state => state.userLogin)
  const {userInfo} = userLogin

  const userUpdateProfile = useSelector(state => state.userUpdateProfile)
  const {success} = userUpdateProfile

  useEffect(() => {
    if(!user.name || user.id !== Number(userId)){
    dispatch(getUserDetails(userId))
    }else{
      setName(user.name)
      setEmail(user.email)
      setLocation(user.location)
      setBio(user.bio)
      setIsCandidate(user.isCandidate)
      setIsAdmin(user.isAdmin)
    }

  }, [])

  const updateSubmitHandler = (e) => {
    e.preventDefault();




  };
  return (

    <div>
    <Link to="/admin/users/">
    <button  style={{position:'absolute',left:'200px' ,outline:'none'}} type="button" className="btn btn-outline-secondary">GO BACK</button>
    </Link>
    <form onSubmit={updateSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>
        {loading? <LoadingSpinner/>:error?<Card>{error}</Card>:


        <div className='form-all'>
        <h2>EDIT USER  </h2>

        <div className="form-control">
      <label htmlFor="username">Name</label>
      <input 
      type="text" 
      id="username" 
      placeholder="Enter Name" 
      value={name}
      onChange={(e) => setName(e.target.value)} />

    </div>
    <div className="form-control">
      <label htmlFor="email">Email</label>
      <input type="email"
      id="email" 
      placeholder="Enter Email" 
      value={email}
      onChange={(e) => setEmail(e.target.value)}/>
    </div>

    <div className="form-control">
      <label htmlFor="address">Address</label>
      <input type="text" 
      id="address"
      placeholder="Enter Address" 
      value={location}
      onChange={(e) => setLocation(e.target.value)} />
    </div>

    <div className="form-control">
      <label htmlFor="bio">Bio</label>
      <textarea type="text" 
      id="bio" 
      placeholder="Write a Bio"  
      value={bio}
      onChange={(e) => setBio(e.target.value)}/>
    </div>
    <div className="form-control">
    <div className="form-check form-switch">
      <input checked={isCandidate} onChange={(e) => setIsCandidate(e.target.value)} className="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
      <label className="form-check-label" for="flexSwitchCheckDefault">IsCandidate</label>
    </div>

  </div>
    <div className="form-control">
    <div className="form-check form-switch">
    <input checked={isAdmin}  onChange={(e) => setIsAdmin(e.target.value)} className="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
    <label className="form-check-label" for="flexSwitchCheckDefault">IsAdmin</label>

    </div>
    </div>

        <Button type='submit'>
          SAVE UPDATE
        </Button>




</div>
      }

        </div>
      </div>
    </form>
    </div>

  );
}



export default EditUserScreen;
