import { Link } from 'react-router-dom';
import './PostItem.css';
import React from "react";
const PostItem = (props) => {

  return (
    <div className='posts'>
      <Link to={`/post/${props.id}`}>
        <div className='description'>
          <img src={props.image} alt={props.title} />
          <h4 >{props.title}</h4>
          <h6>
            {' '}
            {props.numCandidates === 1 ? 'Application' : 'Applications'} :
            <span
              style={{ color: props.numCandidates === 10 ? '#fcc102' : 'green' }}>
              {props.numCandidates}
            </span>
          </h6>
          <h6>
            Post:
            {
              <span
                style={{ color: props.numCandidates === 10 ? 'red' : '#fcc102' }}>
                {props.numCandidates === 10 ? 'Closed' : 'open'}
              </span>
            }
          </h6>
          <p>{props.description.slice(0,60)}</p>

            <button>More info</button>

            <button className='apply'>Apply</button>

        </div>
      </Link>
    </div>
  );
};

export default PostItem;
