import {
  AppBar,
  Grid,
  Toolbar,
  IconButton,
  Badge,
  makeStyles,
} from '@material-ui/core';
import React from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import NotificationsNoneIcon from '@material-ui/icons/NotificationsNone';
import ChatBubbleOutlineIcon from '@material-ui/icons/ChatBubbleOutline';
import PowerSettingsNewIcon from '@material-ui/icons/PowerSettingsNew';
import Link from 'react-router-dom'

const useStyles = makeStyles({
  root: {
    backgroundColor: '#fff',
    transform: 'translateZ(0)',
  },
  searchInput: {
    opacity: '0.6',
    padding: '0 8px',
    fontSize: '0.8rem',
    '&:hover': {
      backgroundColor: '#f2f2f2',
    },
    '& .MuiSvgIcon-root': {
      marginRight: '8px',
    },
  },
  btnRoot: {
    backgroundColor: 'red',
  },
  btnLabel: {
    backgroundColor: 'green',
  },
});
const Header = () => {
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position='static' className={classes.root}>
      <Toolbar>
        <Grid container alignItems='center'>
          <Grid item>
          <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
          Open Menu
        </Button>
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          <MenuItem onClick={handleClose}><Link to="/admin/users/">USERS</Link></MenuItem>
          <MenuItem onClick={handleClose}><Link to="/admin/posts/">POSTS</Link></MenuItem>
          <MenuItem onClick={handleClose}><Link to="/admin/voting/">VOTING RESULTS</Link></MenuItem>
        </Menu>
          </Grid>
          <Grid item sm></Grid>
          <Grid item>
            <IconButton>
              <Badge badgeContent={4} color='secondary'>
                <NotificationsNoneIcon fontSize='small' />
              </Badge>
            </IconButton>
            <IconButton>
              <Badge badgeContent={3} color='primary'>
                <ChatBubbleOutlineIcon  fontSize='small' />
              </Badge>
            </IconButton>
            <IconButton>
              <Badge color='secondary'>
                <PowerSettingsNewIcon />
              </Badge>
            </IconButton>
          </Grid>
        </Grid>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
