import React from 'react';
import './CandidateItem.css';
import { Link, withRouter } from 'react-router-dom';
const CandidatesItems = (props) => {
  return (
    <div>
      {props.isPass && (
        <div className='posts'>
          <Link to='/post'>
            <div className='description'>
              <div>
                <img src={props.candidate_image} alt={props.name} />

                <h2>
                  <span style={{ color: 'green' }}>Email: </span>
                  {props.candidate_email}
                </h2>
                <h2>
                  <span style={{ color: 'green' }}>Email: </span>
                  {props.name}
                </h2>

                <h2>
                  <span style={{ color: 'green' }}>Applied For: </span>
                  {props.applyingFor}
                </h2>

                <p>
                  <span style={{ color: 'green' }}>Bio:</span>
                  {props.bio.slice(1, 100)}
                </p>

                <Link to={`/users/${props.id}`}>
                  <button>View Profile</button>
                </Link>
              </div>
            </div>
          </Link>
        </div>
      )}
    </div>
  );
};

export default withRouter(CandidatesItems);
