import {
    CANDIDATES_LIST_REQUEST,
    CANDIDATES_LIST_SUCCESS,
    CANDIDATES_LIST_FAIL,

    // 
    APPLY_CREATE_REQUEST,
    APPLY_CREATE_SUCCESS,
    APPLY_CREATE_FAIL,
    APPLY_CREATE_RESET 

} from "../constants/CandidatesConstants";
import axios from 'axios'


export const listCandidates = () => async (dispatch, getState) => {
    try {
      dispatch({
        type: CANDIDATES_LIST_REQUEST,
      });

      const {
        userLogin: { userInfo },
      } = getState();

      const config = {
        headers: {
          'Content-type': 'application/json',
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      const { data } = await axios.get(`/api/candidates/`, config);

      dispatch({
        type: CANDIDATES_LIST_SUCCESS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: CANDIDATES_LIST_FAIL,
        payload:
          error.response && error.response.data.detail
            ? error.response.data.detail
            : error.message,
      });
    }
  };

  // create post

  export const PostCreate = (candidate) => async (dispatch, getState) => {
    try {
      dispatch({
        type: APPLY_CREATE_REQUEST,
      });

      const {
        userLogin: { userInfo },
      } = getState();

      const config = {
        headers: {
          'Content-type': 'application/json',
          Authorization: `Bearer ${userInfo.token}`,
        },
      };
      const {data} = await axios.post(`api/candidates/${candidate.post}/create/`,candidate, config);

      dispatch({
        type: APPLY_CREATE_SUCCESS,
        payload:data
      });

    } catch (error) {
      dispatch({
        type: APPLY_CREATE_FAIL,
        payload:
          error.response && error.response.data.detail
            ? error.response.data.detail
            : error.message,
      });
    }
  };
