import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getUserDetails } from '../Action/UserActions';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import Card from '../shared/components/UIElements/Card';
import Button from '../shared/components/FormElements/Button';
import './profileScreen.css';
import profile from '../images/profile.svg';
function ProfileScreen({ history, match }) {
  const userId = match.params.id;
  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    if (!userInfo) {
      history.push('/login');
    } else {
      if (
        !user ||
        !user.name ||
        userInfo.id !== user.id ||
        user.id !== Number(userId)
      ) {
        dispatch(getUserDetails(userId));
      }
    }
  }, [dispatch, userInfo, user, userId, history]);

  return (
    <div className='main-post-details_profile'>
      {loading && <LoadingSpinner asOverlay />}
      {error && <Card>{error}</Card>}
      {userInfo.image ? (
        <div className='section-image_profile'>
          <img src={userInfo.image} alt={userInfo.name} />
        </div>
      ) : (
        <div className='section-image_profile'>
          <img src={profile} alt={userInfo.name} />
        </div>
      )}
      <div className='section-description_profile'>
        <h2>
          <span style={{ color: 'green' }}>Name: </span>
          {userInfo.name}
        </h2>
        <h2>
          <span style={{ color: 'green' }}>Address: </span>
          {userInfo.location}
        </h2>

        <h2>
          <span style={{ color: 'green' }}>Role: </span>
          {userInfo.isAdmin
            ? 'Admin'
            : userInfo.isCandidate
            ? 'Candidate'
            : 'User'}
        </h2>
        <h2>
          <span style={{ color: 'green' }}>Email: </span>
          {userInfo.email}
        </h2>

        <p>
          <span style={{ color: 'green' }}>Bio:</span>
          {userInfo.bio}
        </p>
      </div>
      <div className='btn-profile'>
        <div className='btn1'>
          {!user.bio && !user.location ? (
            <Button
              to={`/user/${userInfo.id}/edit`}
              style={{ marginLeft: '30px' }}>
              COMPLETE PROFILE
            </Button>
          ) : (
            <Button
              to={`/user/${userInfo.id}/edit`}
              style={{ marginLeft: '30px' }}>
              EDIT
            </Button>
          )}
        </div>
        <div className='btn2'>
          <Button inverse>DELETE</Button>
        </div>
      </div>
    </div>
  );
}

export default ProfileScreen;
