import React, { useEffect } from 'react';
import { Grid } from '@material-ui/core';
import { Useform, Form } from '../../Components/Useform';
import Controls from '../../Components/controls/Controls';
import * as employeeServices from '../../services/EmployeesService';

const GenderItem = [
  { id: 'male', title: 'male' },
  { id: 'female', title: 'Female' },
  { id: 'other', title: 'Other' },
];
const initialValues = {
  id: 0,
  name: '',
  email: '',
  departmentId: '',
  isCandidate: false,
  isAdmin: false,
};

const EmployeesForm = (props) => {
  const { addOrEdit, recordForEdit } = props;
  const validate = (fieldsValues = values) => {
    let temp = { ...errors };
    if ('name' in fieldsValues)
      temp.fullName = fieldsValues.fullName ? '' : 'This field is required.';
    if ('email' in fieldsValues)
      temp.email = /$^|.+@.+..+/.test(fieldsValues.email)
        ? ''
        : 'Email is not field';
    if ('departmentId' in fieldsValues)
      temp.departmentId =
        fieldsValues.departmentId.length !== 0 ? '' : 'This field is required';
    setErrors({
      ...temp,
    });
    if (fieldsValues === values)
      return Object.values(temp).every((x) => x === '');
  };
  const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetForm,
  } = Useform(initialValues, true, validate);
  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate()) {
      addOrEdit(values, resetForm);
    }
  };
  useEffect(() => {
    if (recordForEdit !== null) {
      setValues({
        ...recordForEdit,
      });
    }
  }, [recordForEdit, setValues]);
  return (
    <Form onSubmit={handleSubmit}>
      <Grid container>
        <Grid item xs={6}>
          <Controls.Input
            name='name'
            label='Name'
            value={values.name}
            error={errors.name}
            onChange={handleInputChange}
          />
          <Controls.Input
            label='Email'
            name='email'
            value={values.email}
            onChange={handleInputChange}
            error={errors.email}
          />
        </Grid>
        <Grid item xs={6}>
          <Controls.RadioGroup
            name='gender'
            label='Gender'
            value={values.gender}
            onChange={handleInputChange}
            items={GenderItem}
          />
          <Controls.Select
            name='departmentId'
            label='Department'
            value={values.departmentId}
            onChange={handleInputChange}
            options={employeeServices.getGetDepartmentCollection()}
            error={errors.departmentId}
          />

          <Controls.CheckBox
            name='isPermanent'
            label='permanent Employee'
            value={values.isPermanent}
            onChange={handleInputChange}
          />
          <Controls.CheckBox
            name='isAdmin'
            label='Admin'
            value={values.isPermanent}
            onChange={handleInputChange}
          />
          <div>
            <Controls.Button type='submit' text='Submit' />
            <Controls.Button onClick={resetForm} color='default' text='Reset' />
          </div>
        </Grid>
      </Grid>
    </Form>
  );
};

export default EmployeesForm;
