import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import DashboardIcon from '@material-ui/icons/Dashboard';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import HelpIcon from '@material-ui/icons/Help';
import profile from '../../../images/profile.svg';
// import logo from '../../../images/tante-01.svg';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
// import profile from '../../../images/profile.svg';
import './MainNavigation.css';
import MainHeader from './MainHeader';
import SideDrawer from './SideDrawer';
import Backdrop from '../UIElements/Backdrop';
import NavLink from './NavLinks';
const MainNavigation = () => {
  const [drawerIsOpen, setDrawerIsOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const openDrawerHandle = () => {
    setDrawerIsOpen(true);
  };
  const closeDrawerHandle = () => {
    setDrawerIsOpen(false);
  };

  const StyledMenu = withStyles({
    paper: {
      border: '1px solid #d3d4d5',
    },
  })((props) => (
    <Menu
      elevation={0}
      getContentAnchorEl={null}
      anchorOrigin={{
        vertical: 'bottom',
        horizontal: 'center',
      }}
      transformOrigin={{
        vertical: 'top',
        horizontal: 'center',
      }}
      {...props}
    />
  ));

  const StyledMenuItem = withStyles((theme) => ({
    root: {
      '&:focus': {
        backgroundColor: theme.palette.primary.main,
        '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
          color: theme.palette.common.white,
        },
      },
    },
  }))(MenuItem);
  return (
    <React.Fragment>
      <MainHeader>
        {drawerIsOpen && <Backdrop onClick={closeDrawerHandle} />}

        <SideDrawer show={drawerIsOpen} onClick={closeDrawerHandle}>
          <nav className='main-navigation__drawer-nav'>
            <NavLink />
          </nav>
        </SideDrawer>

        <button
          className='main-navigation__menu-btn'
          onClick={openDrawerHandle}>
          {' '}
          <span />
          <span />
          <span />{' '}
        </button>
        <h1 className='main-navigation__title'>
          <Link to='/'>User Name </Link>
        </h1>
        <div>
          <Button
            style={{
              backgroundColor: 'transparent',
              borderRadius: '10%',
              position: 'absolute',
              right: '10px',
              top: '19px',
            }}
            aria-controls='customized-menu'
            aria-haspopup='true'
            onClick={handleClick}>
            <img
              style={{ padding: '10px' }}
              className='image-profile'
              src={profile}
              alt='profile'
            />
          </Button>
          <StyledMenu
            id='customized-menu'
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}>
            <Link to='/user/profile'>
              <StyledMenuItem onClick={handleClose}>
                <ListItemIcon>
                  <AccountCircleIcon fontSize='small' />
                </ListItemIcon>
                <ListItemText primary='PROFILE' />
              </StyledMenuItem>
            </Link>
            <Link to='/admin'>
              <StyledMenuItem onClick={handleClose}>
                <ListItemIcon>
                  <DashboardIcon fontSize='small' />
                </ListItemIcon>
                <ListItemText primary='DASHBOARD' />
              </StyledMenuItem>
            </Link>
            <Link to='/admin'>
              <StyledMenuItem onClick={handleClose}>
                <ListItemIcon>
                  <HelpIcon fontSize='small' />
                </ListItemIcon>
                <ListItemText primary='Q&A' />
              </StyledMenuItem>
            </Link>

            <StyledMenuItem>
              <ListItemIcon>
                <ExitToAppIcon fontSize='small' />
              </ListItemIcon>
              <ListItemText primary='LOGOUT' />
            </StyledMenuItem>
          </StyledMenu>
        </div>
        <nav className='main-navigation__header-nav'>
          <NavLink />
        </nav>
      </MainHeader>
    </React.Fragment>
  );
};

export default MainNavigation;
