import React, { useState } from 'react';
import EmployeesForm from './EmployeesForm';
import Controls from '../../Components/controls/Controls';
import SearchIcon from '@material-ui/icons/Search';
import Popup from '../../Components/Popup';
import Notification from '../../Components/Notification';
import ConfirmDialog from '../../Components/ConfirmDialog';
import * as employeeServices from '../../services/EmployeesService';

import {
  Paper,
  makeStyles,
  TableBody,
  TableRow,
  TableCell,
  Toolbar,
  InputAdornment,
} from '@material-ui/core';
import useTable from '../../Components/useTable';
import * as employeeService from '../../services/EmployeesService';
import AddIcon from '@material-ui/icons/Add';
import { EditOutlined, DeleteOutline } from '@material-ui/icons';
const useStyles = makeStyles((theme) => ({
  pageContent: {
    margin: theme.spacing(5),
    padding: theme.spacing(3),
  },
  searchInput: {
    width: '75%',
  },
  newButton: {
    position: 'absolute',
    right: '10px',
  },
}));

const headCells = [
  { id: 'fullName', label: 'Employee Name' },
  { id: 'email', label: 'Email Address (Personal)' },
  { id: 'mobile', label: 'Mobile Number' },
  { id: 'department', label: 'Department ', disableSorting: true },
  { id: 'actions', label: 'Actions ', disableSorting: true },
];

const Employees = () => {
  const classes = useStyles();

  const [recordForEdit, setRecordForEdit] = useState(null);
  const [records, setRecords] = useState(employeeService.getAllEmployees());
  const [filterFn, setFilterFn] = useState({
    fn: (items) => {
      return items;
    },
  });
  const [openPopup, setOpenPopup] = useState(false);
  const [confirmDialog, setConfirmDialog] = useState({
    isOpen: false,
    title: '',
    subTitle: '',
  });
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });

  const {
    TblPagination,
    TblContainer,
    TblHeader,
    recordsAfterPaginationSorting,
  } = useTable(records, headCells, filterFn);

  const handleSearch = (e) => {
    let target = e.target;
    setFilterFn({
      fn: (items) => {
        if (target.value === '') {
          return items;
        } else {
          return items.filter((x) => x.fullName.includes(target.value));
        }
      },
    });
  };
  const addOrEdit = (employee, resetForm) => {
    if (employee.id === 0) {
      employeeServices.insertEmployee(employee);
    } else {
      employeeServices.updateEmployee(employee);
      setRecordForEdit(null);
      resetForm();
      setOpenPopup(false);
      setRecords(employeeService.getAllEmployees());
      setNotify({
        isOpen: true,
        message: 'Submitted Successfully',
        type: 'success',
      });
    }
  };

  const openInPopup = (item) => {
    setRecordForEdit(item);
    setOpenPopup(true);
  };

  const onDelete = (id) => {
    setConfirmDialog({
      ...confirmDialog,
      isOpen: false,
    });
    employeeServices.deleteEmployee(id);
    setRecords(employeeService.getAllEmployees());
    setNotify({
      isOpen: true,
      message: 'Deleted Successfully',
      type: 'error',
    });
  };
  return (
    <>


      <Paper className={classes.pageContent}>
        <Toolbar>
          <Controls.Input
            className={classes.searchInput}
            label='Search Employees'
            InputProps={{
              startAdornment: (
                <InputAdornment position='start'>
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
            onChange={handleSearch}
          />
          <Controls.Button
            className={classes.newButton}
            text='Add New'
            variant='outlined'
            startIcon={<AddIcon />}
            onClick={() => setOpenPopup(true)}
          />
        </Toolbar>
        <TblContainer>
          <TblHeader />
          <TableBody>
            {recordsAfterPaginationSorting().map((item) => (
              <TableRow key={item.id}>
                <TableCell>{item.fullName}</TableCell>
                <TableCell>{item.email}</TableCell>
                <TableCell>{item.mobile}</TableCell>
                <TableCell>{item.departmentId}</TableCell>
                <TableCell>
                  <Controls.ActionButton color='primary'>
                    <EditOutlined
                      fontsize='small'
                      onClick={() => {
                        openInPopup(item);
                      }}
                    />
                  </Controls.ActionButton>
                  <Controls.ActionButton
                    onClick={() => {
                      setConfirmDialog({
                        isOpen: true,
                        title: 'Are you sure to delete this record?',
                        subTitle: "You can't undo this operation!",
                        onConfirm: () => {
                          onDelete(item.id);
                        },
                      });
                      // onDelete(item.id);
                    }}
                    color='secondary'>
                    <DeleteOutline fontsize='small' />
                  </Controls.ActionButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </TblContainer>
        <TblPagination />
      </Paper>
      <Popup
        openPopup={openPopup}
        setOpenPopup={setOpenPopup}
        title='Employee Form'>
        <EmployeesForm recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </Popup>
      <Notification notify={notify} setNotify={setNotify} />
      <ConfirmDialog
        confirmDialog={confirmDialog}
        setConfirmDialog={setConfirmDialog}
      />
    </>
  );
};

export default Employees;
