import React, { useEffect, useState } from 'react';
import Input from '../shared/components/FormElements/Input';
import Button from '../shared/components/FormElements/Button';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE,
} from '../shared/util/validators';
import { useForm } from '../shared/hooks/form-hook';
import { getUserDetails, updateUserProfile } from '../Action/UserActions';
function EditProfileScreen({ history }) {

  const [message, setMessage] = useState('');

  const dispatch = useDispatch()

  const useDetails = useSelector(state => state.useDetails)
  const {loading,error,user} = useDetails

  const userLogin = useSelector(state => state.userLogin)
  const {userInfo} = userLogin

  const userUpdateProfile = useSelector(state => state.userUpdateProfile)
  const {success} = userUpdateProfile




  const [formState, inputHandler,setFormData] = useForm(
    {
      email: {
        value: '',
        isValid: false,
      },
      password: {
        value: '',
        isValid: false,
      },
      passwordConfirm: {
        value: '',
        isValid: false,
      },
      name: {
        value: '',
        isValid: false,
      },

      location: {
        value: '',
        isValid: false,
      },

      image: {
        value: '',
        isValid: false,
      },

      bio: {
        value: '',
        isValid: false,
      },
    },
    false
  );

  useEffect(() => {
    if(!userInfo){
  history.push('/login')
    }else{
      if(!user || !user.name||success){
        dispatch(getUserDetails('profile'))

      }else{
        setFormData({
          email: {
            value: user.email,
            isValid: true,
          },
          password: {
            value: user.password,
            isValid: true,
          },
          passwordConfirm: {
            value: user.password,
            isValid: true,
          },
          name: {
            value: user.name,
            isValid: true,
          },

          location: {
            value: user.location,
            isValid: true,
          },

          bio: {
            value: user.bio,
            isValid: true,
          },

        },true)
      }
    }


  }, [dispatch,userInfo,user,history,setFormData,success])

  const updateSubmitHandler = (e) => {
    e.preventDefault();
    if( formState.inputs.password.value !== formState.inputs.passwordConfirm.value){
      setMessage(" Password don't match")

    }else{
      dispatch(updateUserProfile({
        'id':user.id,
        'name':formState.inputs.name.value,
        'email':formState.inputs.email.value,
        'location':formState.inputs.location.value,
        'bio':formState.inputs.bio.value,
        'password':formState.inputs.password.value,
      }))
      history.push('/user/profile')
    }

  };
  return (
    <form onSubmit={updateSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>
          <div className='form-all'>
            <h2>EDIT YOUR PROFILE  </h2>
            {message&&<Card className='error_message'>{message}</Card>}
            {error && <Card className='error_message'>{error}</Card>}
            {loading && <LoadingSpinner asOverlay />}
            <Input
              element='input'
              id='name'
              type='text'
              label='Name'
              placeholder='Enter Name'
              validators={[VALIDATOR_REQUIRE()]}
              errorText='Name Required'
              onInput={inputHandler}
              initialValue={user.name}
              initialValid={true}
            />
            <Input
              element='input'
              id='email'
              type='email'
              label='E-Mail'
              placeholder='Enter E-Mail'
              validators={[VALIDATOR_EMAIL()]}
              errorText='Please enter a valid email address.'
              onInput={inputHandler}
              initialValue={user.email}
              initialValid={true}


            />

            <Input
            element='input'
            id='location'
            type='text'
            label='Address(Country/City)'
            placeholder='Enter Address'
            errorText='Address Required'
            validators={[VALIDATOR_REQUIRE()]}
            onInput={inputHandler}
            initialValue={user.location}
            initialValid={true}


          />
          <Input
          element=''
          id='bio'
          type='text'
          label='Bio'
          placeholder='Enter Bio'
          validators={[VALIDATOR_REQUIRE()]}
          errorText='Bio Required'
          onInput={inputHandler}
          initialValue={user.bio}
          initialValid={true}


        />

            <Input
              element='input'
              id='password'
              type='password'
              label='Password'
              placeholder='Enter Password'
              validators={[VALIDATOR_MINLENGTH(6)]}
              errorText='Please enter a valid password, at least 6 characters.'
              onInput={inputHandler}



            />
            <Input
              element='input'
              id='passwordConfirm'
              type='password'
              label='Confirm Password'
              placeholder='Enter Password again'
              validators={[VALIDATOR_MINLENGTH(6)]}
              errorText="Password don't match"
              onInput={inputHandler}



            />
            <Button type='submit' disabled={!formState.isValid}>
              SAVE PROFILE
            </Button>




          </div>
        </div>
      </div>
    </form>
  );
}



export default EditProfileScreen;
