import React,{useEffect,useState} from 'react'
import {useSelector,useDispatch} from 'react-redux'
import {Link} from 'react-router-dom'
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner'
import Card  from '../shared/components/UIElements/Card'
import SearchIcon from '@material-ui/icons/Search';

import './UserListScreen.css'
import AddIcon from '@material-ui/icons/Add';
import {

    makeStyles,

    InputAdornment,
  } from '@material-ui/core';
import { deleteUser } from '../Action/UserActions'
import { EditOutlined, DeleteOutline } from '@material-ui/icons';
import Controls from '../shared/controls/Controls'
import Notification from '../shared/components/UIElements/Notification'
import ConfirmDialog from '../shared/components/UIElements/ConfirmDialog'
import { listPosts } from '../Action/PostActions'
// import Popup from '../shared/components/UIElements/Popup'
// import EmployeesForm from '../shared/components/UIElements/EmployeesForm'

const useStyles = makeStyles((theme) => ({
    pageContent: {
      margin: theme.spacing(5),
      padding: theme.spacing(3),
      marginTop:theme.spacing(-30)
    },
    searchInput: {
      width: '75%',
    },
    newButton: {
      position: 'absolute',
      right: '10px',
    },
  }));

function PostListScreen({history}) {
    const classes = useStyles();
    const [notify, setNotify] = useState({
        isOpen: false,
        message: '',
        type: '',
      });
      const [confirmDialog, setConfirmDialog] = useState({
        isOpen: false,
        title: '',
        subTitle: '',
      });
const dispatch = useDispatch()
const postList = useSelector(state => state.postList)
const {posts,loading,error} = postList

const userLogin = useSelector(state => state.userLogin)
const {userInfo} = userLogin

const userDelete = useSelector(state => state.userDelete)
const {success} = userDelete

useEffect(() => {
    if(userInfo && userInfo.isAdmin){
        dispatch(listPosts())
    }else{
        history.push('/login')
    }



}, [dispatch,userInfo,history,success])

const deleteHandler =  (id) =>{
        dispatch(deleteUser(id))
        if(success){
            setConfirmDialog({
                ...confirmDialog,
                isOpen: false,
              });
         setNotify({
             isOpen: true,
             message: 'Deleted Successfully',
             type: 'error',
           });


    }

}
    return (
        <div className="user__list">
        <h2 style={{fontSize:"23px"}}>ALL POSTS LIST</h2>

      <Controls.Button
        className={classes.newButton}
        text='Add New'
        variant='outlined'
        startIcon={<AddIcon />}
        onClick={''}
      />

        {loading ?<LoadingSpinner asOverlay/>:error?<Card>{error}</Card>:
        <table>
        <tbody>
        <tr>
            <th>#</th>
            <th>Title</th>
            <th>Description</th>
            <th>Applications</th>
            <th>CreatedAt</th>
            <th>Active</th>
            <th>Actions</th>
        </tr>
        {posts.map((post)=>(
            <tr key={post.id}>
            <td>{post.id}</td>
            <td>{post.title}</td>
            <td>{post.description.slice(1,80)}...</td>
            <td>{post.applications}</td>
            <td>{post.createdAt}</td>
            <td>{post.isActive?'Yes':'No'}</td>
            <td>
            <Link to={`/admin/user/${post.id}/edit`}>
            <Controls.ActionButton color='primary'>
            <EditOutlined
              fontSize='small'
            />
          </Controls.ActionButton>
          </Link>
          <Controls.ActionButton
            color='default'>
            <DeleteOutline fontSize='small' 
            onClick={() => {
                setConfirmDialog({
                  isOpen: true,
                  title: 'Are you sure to delete this Post?',
                  subTitle: "You can't undo this operation!",
                  onConfirm: () => {
                    deleteHandler(post.id);
                  },
                });
                // onDelete(item.id);
              }}

            />
          </Controls.ActionButton>
            </td>
        </tr>
        ))}     
        </tbody> 
        </table>

    }

    <Notification notify={notify} setNotify={setNotify} />
    <ConfirmDialog
    confirmDialog={confirmDialog}
    setConfirmDialog={setConfirmDialog}
  />

        </div>
    )
}

export default PostListScreen
