import { Link,NavLink } from 'react-router-dom';
import './PostItem.css';
import React from "react";
const PostItem = (props) => {

  if(props.Active){}
  return (
    <div className='posts'>
      <NavLink to={`/post/${props.id}`}>
        <div className='description'>
          <img src={props.image} alt={props.title} />
          <h4 >{props.title}</h4>
          <h6>
            {' '}
            {props.applications === 1 ? 'Application' : 'Applications'} :
            <span
              style={{ color: props.applications === 10 ? '#fcc102' : 'green' }}>
              {props.applications}
            </span>
          </h6>
          <h6>
            Post:
            {
              <span
                style={{ color: props.applications === 10 ? 'red' : '#fcc102' }}>
                {props.applications === 10 ? 'Closed' : 'open'}
              </span>
            }
          </h6>
          <p>{props.description.slice(0,60)}</p>
          <Link to={`/post/${props.id}`}> <button>More info</button></Link>

         <Link to="/apply"><button className='apply'>Apply</button></Link>


        </div>
      </NavLink>
    </div>
  );
};

export default PostItem;
