import React, { useEffect } from 'react';
import './CandidateItem.css';
import profile from '../../images/user.png';
import { Link, withRouter } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { listCandidates } from '../../Action/CandidateActions';

const CandidatesItems = (history) => {
  const dispatch = useDispatch();

  const candidatesList = useSelector((state) => state.candidatesList);
  const { error, loading, candidates } = candidatesList;

  useEffect(() => {
    dispatch(listCandidates());
  }, [dispatch]);

  return (
    <>
      {candidates.map((candidate) => (
        <div key={candidate.id}>
          {candidate.isPass && (
            <div className='posts'>
              <Link to='/post'>
                <div className='description'>
                  <div>
                    <img src={profile} alt={candidate.name} />
                    <span style={{ marginLeft: '20px', fontSize: '12px' }}>
                      <span style={{ color: 'green' }}>Name:</span>
                      {candidate.name}
                    </span>
                    <h4 style={{ fontSize: '12px' }}>
                      <spn style={{ color: '#000' }}>Applied For: </spn>
                      <sp>{candidate.title}</sp>
                    </h4>
                    <h4 style={{ fontSize: '12px' }}>
                      <span style={{ color: '#000' }}>E-mail:</span>{' '}
                      {candidate.candidate_email}
                    </h4>
                    <p
                      style={{
                        fontSize: '15px',
                        width: '90%',
                        marginLeft: '10px',
                        fontFamily: 'sans-serif',
                      }}>
                      <span style={{ marginRight: '10px', color: 'seagreen' }}>
                        Bio:
                      </span>
                      {candidate.bio.slice(1, 100)}
                    </p>
                    <Link to={`/profile/candidates/${candidate.id}`}>
                      <button>proceed to vote</button>
                    </Link>
                  </div>
                </div>
              </Link>
            </div>
          )}
        </div>
      ))}
    </>
  );
};

export default withRouter(CandidatesItems);
