import React from 'react';
import ReactDom from 'react-dom';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import {Link} from 'react-router-dom'
import Button from '@material-ui/core/Button';

import PostListScreen from './PostListScreen'
import {
  CssBaseline,
  makeStyles,
  createMuiTheme,
  ThemeProvider,
} from '@material-ui/core';
import Header from '../TanteAdmin/Components/Header';


const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#2B3053',
      light: '#3c44b126',
    },
    secondary: {
      main: '#f83245',
      light: '#f8324526',
    },
    background: {
      default: '#f4f5fd',
    },
  },
  overrides: {
    MuiAppBar: {
      root: {
        transform: 'translateZ(0)',
      },
    },
  },
  props: {
    MuiIconButton: {
      disableRipple: true,
    },
  },
  shape: {
    borderRadius: '5px',
  },
});

const useStyles = makeStyles({
  appMain: {
    paddingLeft: '20px',
    width: '100%',
    marginTop: '100px',
  },
});
function AdminPostsScreen() {
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const contentAdmin = (
    <ThemeProvider theme={theme}>
      <React.Fragment>
        <div className={classes.appMain}>
        <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
        Open Menu
      </Button>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem onClick={handleClose}><Link to="/admin/users/">USERS</Link></MenuItem>
        <MenuItem onClick={handleClose}><Link to="/admin/posts/">POSTS</Link></MenuItem>
        <MenuItem onClick={handleClose}><Link to="/admin/voting/">VOTING RESULTS</Link></MenuItem>
      </Menu>


          <PostListScreen/>

        </div>

        <CssBaseline />
      </React.Fragment>
    </ThemeProvider>
  );
  return ReactDom.createPortal(
    contentAdmin,
    document.getElementById('admin-page')
  );
}

export default AdminPostsScreen;
