import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import './EditProfileScreen.css';
import Notification from '../shared/components/UIElements/Notification';
import { PostUpdate } from '../Action/PostActions';
import { POST_UPDATE_RESET } from '../constants/PostConstants';
import { getCandidateDetails } from '../Action/CandidateActions';

function ApplyScreen({ match, history }) {
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });
  const candidateId = match.params.id;
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [isActive, setIsActive] = useState(false);
  const [isApplying, setIsApplying] = useState(false);

  const dispatch = useDispatch();

  const candidateDetails = useSelector((state) => state.candidateDetails);
  const { loading, error, candidate } = candidateDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const postUpdate = useSelector((state) => state.postUpdate);
  const {
    success: successUpdate,
    error: errorUpdate,
    loading: loadingUpdate,
  } = postUpdate;

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: POST_UPDATE_RESET });
      history.push('/admin/candidates');
    } else {
      if (
        !userInfo ||
        !candidate.name ||
        candidate.id !== Number(candidateId)
      ) {
        dispatch(getCandidateDetails(candidateId));
      } else {
        setTitle(userInfo.name);
        setDescription(candidate.description);
        setIsActive(candidate.isActive);
        setIsApplying(candidate.isApplying);
      }
    }
  }, [dispatch, history, successUpdate, userInfo, candidate, candidateId]);

  const updateSubmitHandler = (e) => {
    e.preventDefault();

    dispatch(
      PostUpdate({
        id: candidateId,
        title,
        description,
        isActive,
        isApplying,
      })
    );
    setNotify({
      isOpen: true,
      message: 'Submitted Successfully',
      type: 'success',
    });
  };
  return (
    <div>
      <Link to='/admin/posts/'>
        <button
          style={{ position: 'absolute', left: '200px', outline: 'none' }}
          type='button'
          className='btn btn-outline-secondary'>
          GO BACK
        </button>
      </Link>
      <form onSubmit={updateSubmitHandler}>
        <div className='login-form-register'>
          <div className='container-form'>
            {errorUpdate && <Card>{errorUpdate}</Card>}
            {loadingUpdate && <LoadingSpinner asOverlay />}
            {loading ? (
              <LoadingSpinner />
            ) : error ? (
              <Card>{error}</Card>
            ) : (
              <div className='form-all'>
                <h2>EDIT POST </h2>

                <div className='form-control'>
                  <label htmlFor='title'>Post Title</label>
                  <input
                    type='text'
                    id='title'
                    placeholder='Enter Post Title'
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>
                <div className='form-control'>
                  <label htmlFor='description'>Description</label>
                  <textarea
                    type='text'
                    id='description'
                    placeholder='Write a Description'
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                  />
                </div>
                <div className='form-control'>
                  <div className='form-check form-switch'>
                    <input
                      checked={isActive}
                      onChange={(e) => setIsActive(e.target.checked)}
                      className='form-check-input'
                      type='checkbox'
                      id='flexSwitchCheckDefault'
                    />
                    <label
                      className='form-check-label'
                      htmlFor='flexSwitchCheckDefault'>
                      Active
                    </label>
                  </div>
                </div>
                <div className='form-control'>
                  <div className='form-check form-switch'>
                    <input
                      checked={isApplying}
                      onChange={(e) => setIsApplying(e.target.checked)}
                      className='form-check-input'
                      type='checkbox'
                      id='flexSwitchCheckDefaults'
                    />
                    <label
                      className='form-check-label'
                      htmlFor='flexSwitchCheckDefaults'>
                      Users Application{' '}
                    </label>
                  </div>
                </div>

                <Button type='submit'>SAVE UPDATES</Button>
              </div>
            )}
          </div>
        </div>
      </form>
      <Notification notify={notify} setNotify={setNotify} />
    </div>
  );
}

export default ApplyScreen;
