import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import { PostList } from '../components/PostList';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import { useHttpClient } from '../../shared/hooks/http-hook';
import { postDetails } from '../../Action/PostActions';

const UserPlaces = () => {
  const [loadedPlaces, setLoadedPlaces] = useState([
    {
      id: 1,
      title: 'Manager',
      description:
        'Duis mollis, est non commodo luctus, nisi erat porttitor ligula Integer posuere erat a ante venenatis dapibus posuere velit aliquet.',
      image: '/images/camera.jpg',
      candidates: 5,
      createdAt: '2021-03-20',
      user: 1,
    },
    {
      id: 2,
      title: 'HR',
      description:
        'Duis mollis, est non commodo luctus, nisi erat porttitor ligula Integer posuere erat a ante venenatis dapibus posuere velit aliquet.',
      image: '/images/alexa.jpg',
      candidates: 3,
      createdAt: '2021-03-20',
      user: 1,
    },
  ]);
  const { isLoading, error, clearError } = useHttpClient();

  const userId = useParams().userId;
  const dispatch = useDispatch();
  const postDetail = useSelector((state) => state.postDetail);
  const { post } = postDetail;
  useEffect(() => {
    dispatch(postDetails(userId));
  }, [dispatch, userId]);

  const placeDeletedHandler = (deletedPlaceId) => {
    setLoadedPlaces((prevPlaces) =>
      prevPlaces.filter((place) => place.id !== deletedPlaceId)
    );
  };

  return (
    <React.Fragment>
      <ErrorModal error={error} onClear={clearError} />
      {isLoading && (
        <div className='center'>
          <LoadingSpinner />
        </div>
      )}
      {!isLoading && loadedPlaces && (
        <PostList items={loadedPlaces} onDeletePlace={placeDeletedHandler} />
      )}
    </React.Fragment>
  );
};

export default UserPlaces;
