import React, { useState, useEffect } from 'react';
import EmployeesForm from './EmployeesForm';

import Controls from '../../Components/controls/Controls';
import SearchIcon from '@material-ui/icons/Search';
import Popup from '../../Components/Popup';
import Notification from '../../Components/Notification';
import ConfirmDialog from '../../Components/ConfirmDialog';
import * as employeeServices from '../../services/EmployeesService';

import {
  Paper,
  makeStyles,
  TableBody,
  TableRow,
  TableCell,
  Toolbar,
  InputAdornment,
} from '@material-ui/core';
import useTable from '../../Components/useTable';

import AddIcon from '@material-ui/icons/Add';
import { EditOutlined, DeleteOutline } from '@material-ui/icons';
import { useSelector, useDispatch } from 'react-redux';
import { listUsers } from '../../../Action/UserActions';

const useStyles = makeStyles((theme) => ({
  pageContent: {
    margin: theme.spacing(5),
    padding: theme.spacing(3),
  },
  searchInput: {
    width: '75%',
  },
  newButton: {
    position: 'absolute',
    right: '10px',
  },
}));

const headCells = [
  { id: 'name', label: 'User Name' },
  { id: 'email', label: 'Email Address (Personal)' },
  { id: 'isAdmin', label: 'Status' },
  { id: 'candidate', label: 'Candidate', disableSorting: true },
  // { id: 'department', label: 'Department ', disableSorting: true },
  { id: 'actions', label: 'Actions ', disableSorting: true },
];

const Employees = () => {
  const dispatch = useDispatch();

  const userList = useSelector((state) => state.userList);
  const { loading, error, users } = userList;

  useEffect(() => {
    dispatch(listUsers());
  }, [dispatch]);

  const classes = useStyles();

  const { TblPagination, TblContainer, TblHeader } = useTable(headCells);

  return (
    <>
      <Paper className={classes.pageContent}>
        <Toolbar>
          <Controls.Input
            className={classes.searchInput}
            label='Search Employees'
            InputProps={{
              startAdornment: (
                <InputAdornment position='start'>
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
          <Controls.Button
            className={classes.newButton}
            text='Add New'
            variant='outlined'
            startIcon={<AddIcon />}
          />
        </Toolbar>
        <TblContainer>
          <TblHeader />
          <TableBody>
            {users.map((item) => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.email}</TableCell>
                <TableCell>{item.isAdmin ? 'Yes' : 'No'}</TableCell>
                <TableCell>{item.mobile}</TableCell>
                <TableCell>
                  <Controls.ActionButton color='primary'>
                    <EditOutlined fontsize='small' />
                  </Controls.ActionButton>
                  <Controls.ActionButton color='secondary'>
                    <DeleteOutline fontsize='small' />
                  </Controls.ActionButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </TblContainer>
        <TblPagination />
      </Paper>
    </>
  );
};

export default Employees;
