import React, { useEffect, useState } from 'react';
import Input from '../../shared/components/FormElements/Input';
import Button from '../../shared/components/FormElements/Button';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../../shared/components/UIElements/Card';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';

import {
  VALIDATOR_EMAIL,
  VALIDATOR_MINLENGTH,
  VALIDATOR_REQUIRE,
} from '../../shared/util/validators';
import { useForm } from '../../shared/hooks/form-hook';
import './Register.css';
import { RegisterUser } from '../../Action/UserActions';
function Register({ location, history }) {
  const [message, setMessage] = useState('');
  const redirect = location.search ? location.search.split('=')[1] : '/';

  const dispatch = useDispatch();
  const userRegister = useSelector((state) => state.userRegister);
  const { error, userInfo, loading } = userRegister;

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  const [formState, inputHandler] = useForm(
    {
      email: {
        value: '',
        isValid: false,
      },
      password: {
        value: '',
        isValid: false,
      },
      passwordConfirm: {
        value: '',
        isValid: false,
      },
      name: {
        value: '',
        isValid: false,
      },
    },
    false
  );
  const registerSubmitHandler = (e) => {
    e.preventDefault();
    if (
      formState.inputs.password.value !== formState.inputs.passwordConfirm.value
    ) {
      setMessage('Password do not match');
    } else {
      dispatch(
        RegisterUser(
          formState.inputs.name.value,
          formState.inputs.email.value,
          formState.inputs.password.value,
          formState.inputs.passwordConfirm.value
        )
      );
    }
  };
  return (
    <div className="login-box">
      <div className="left-box">
        <h1>Sign Up</h1>
        <input type="text" name="username" placeholder="Username" />
        <input type="text" name="email" placeholder="Email" />
        <input type="password" name="password" placeholder="Password" />
        <input
          type="password"
          name="password2"
          placeholder="Confirm Password"
        />
        <input type="submit" name="signup-button" value="Sign Up" />
      </div>

      <div className="right-box">
        <span className="signinwith">Sign in with<br />Social Network </span>
        <button className="social facebook">Log in with Facebook</button>
        <button className="social twitter">Log in with Twitter</button>
        <button className="social google">Log in with Google+</button>
      </div>
      <div class="or">OR</div>
    </div>
  );
}



export default Register;
