import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import { USER_UPDATE_PROFILE_RESET } from '../constants/UserConstant';
import './EditProfileScreen.css'
import { getUserDetails } from '../Action/UserActions';

function EditUserScreen({ match, history }) {
 const userId = match.params.id
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [bio, setBio] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [isCandidate, setIsCandidate] = useState(false);


  const dispatch = useDispatch()

  const useDetails = useSelector(state => state.useDetails)
  const {loading,error,user} = useDetails

  const userLogin = useSelector(state => state.userLogin)
  const {userInfo} = userLogin

  const userUpdateProfile = useSelector(state => state.userUpdateProfile)
  const {success} = userUpdateProfile

  useEffect(() => {



  }, [])

  const updateSubmitHandler = (e) => {
    e.preventDefault();




  };
  return (
    <form onSubmit={updateSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>
          <div className='form-all'>
            <h2>EDIT USER  </h2>
            <div className="form-control">
          <label htmlFor="username">Name</label>
          <input 
          type="text" 
          id="username" 
          placeholder="Enter Username" 
          value={name}
          onChange={(e) => setName(e.target.value)} />

        </div>
        <div className="form-control">
          <label htmlFor="email">Email</label>
          <input type="email"
           id="email" 
           placeholder="Enter Email" 
           value={email}
           onChange={(e) => setEmail(e.target.value)}/>
        </div>

        <div className="form-control">
          <label htmlFor="address">Address</label>
          <input type="text" 
          id="address"
           placeholder="Enter Address" 
           value={location}
           onChange={(e) => setLocation(e.target.value)} />
        </div>

        <div className="form-control">
          <label htmlFor="bio">Bio</label>
          <textarea type="text" 
          id="bio" 
          placeholder="Write Bio"  
          value={bio}
          onChange={(e) => setBio(e.target.value)}/>
        </div>
        <div className="form-control">
        <label htmlFor="isCandidate">Candidate</label>
        <input
          type="checkbox"
          className="regular-checkbox"
          id="isCandidate"
          label = 'IsCandidate'
          value={isCandidate}
          onChange={(e) => setIsCandidate(e.target.value)}
        />

      </div>
        <div className="form-control">
          <label htmlFor="isAdmin">Admin</label>
          <input
          className="regular-checkbox"
            type="checkbox"
            id="isAdmin"
            label = 'IsAdmin'
            value={isAdmin}
            onChange={(e) => setIsAdmin(e.target.value)}
          />

        </div>

            <Button type='submit'>
              SAVE UPDATE
            </Button>




          </div>
        </div>
      </div>
    </form>
  );
}



export default EditUserScreen;
