import React,{useEffect} from 'react'
import './PostScreen.css'
import {useSelector,useDispatch} from 'react-redux'
import { listPostDetails } from '../Action/PostActions';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import Card from '../shared/components/UIElements/Card'
import {Link} from 'react-router-dom'


function PostScreen({match}) {
    const dispatch = useDispatch();


  const postDetails = useSelector((state) => state.postDetails);
  const { post, loading, errors } = postDetails;


const candidate =post.candidates
  useEffect(() => {
    dispatch(listPostDetails(match.params.id));
  }, [dispatch, match]);
    return ( 
        <div className="main-post-details">
        {loading && <LoadingSpinner/>}
        {errors && <Card>{errors}</Card>}
        <div className="section-image">
       <img src={post.image} alt={post.title}/>
        </div>
        <div className="section-description">
        <h1><span style={{color:'green'}}>Title: </span>{post.title}</h1>
        <h1>
        Post:
        {
          <span
            style={{ color: post.applications === 10 ? 'red' : 'green' }}>
            {post.applications === 10 ? 'Closed' : 'open'}
          </span>
        }
      </h1>
        <h1><span style={{color:'green'}}>Application: </span> {post.applications}</h1>
        <p><span style={{color:'green'}}>Description:</span> {post.description}</p>

        </div>
            <div className="section-candidate"> 
            <Card>
            <p>{post.title}</p>
            </Card>  
           </div>




      </div>
    )
}

export default PostScreen
