import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import { Link, withRouter } from 'react-router-dom';

import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import './EditProfileScreen.css';
import Notification from '../shared/components/UIElements/Notification';
import { listPostDetails, PostUpdate } from '../Action/PostActions';
import { POST_UPDATE_RESET } from '../constants/PostConstants';
import { getCandidateDetails } from '../Action/CandidateActions';
import { VALIDATOR_REQUIRE, VALIDATOR_EMAIL } from '../shared/util/validators';
import { useForm } from '../shared/hooks/form-hook';
import Input from '../shared/components/FormElements/Input';
function ApplyScreen({ match, history }) {
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });
  const postId = match.params.id;
  const [title, setTitle] = useState('');
  const [email, setEmail] = useState('');
  const [isActive, setIsActive] = useState(false);
  const [isApplying, setIsApplying] = useState(false);

  const dispatch = useDispatch();

  const postDetails = useSelector((state) => state.postDetails);
  const { loading, error, post } = postDetails;

  const candidateDetails = useSelector((state) => state.candidateDetails);
  const { candidate } = candidateDetails;
  console.log(candidate);

  const postUpdate = useSelector((state) => state.postUpdate);
  const {
    success: successUpdate,
    error: errorUpdate,
    loading: loadingUpdate,
  } = postUpdate;

  const [formState, inputHandler, setFormData] = useForm(
    {
      email: {
        value: '',
        isValid: false,
      },
      password: {
        value: '',
        isValid: false,
      },
      passwordConfirm: {
        value: '',
        isValid: false,
      },
      title: {
        value: '',
        isValid: false,
      },
    },
    false
  );

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: POST_UPDATE_RESET });
      history.push('/admin/posts');
    } else {
      if (!post.title || post.id !== Number(postId)) {
        dispatch(listPostDetails(postId));
        dispatch(getCandidateDetails(postId));
      } else {
        setTitle(candidate.title);
        setEmail(candidate.candidate_email);
        setFormData(
          {
            title: {
              value: candidate.title,
              isValid: true,
            },
            email: {
              value: candidate.candidate_email,
              isValid: true,
            },
          },
          true
        );
      }
    }
  }, [dispatch, history, setFormData, candidate, successUpdate, post, postId]);

  const updateSubmitHandler = (e) => {
    e.preventDefault();

    dispatch(
      PostUpdate({
        // id: postId,
        // title,
        // description,
        // isActive,
        // isApplying,
      })
    );
    setNotify({
      isOpen: true,
      message: 'Submitted Successfully',
      type: 'success',
    });
  };
  return (
    <div>
      <form onSubmit={updateSubmitHandler}>
        <div className='login-form-register'>
          <div className='container-form'>
            <div className='form-all'>
              <h2>APPLYING FOR POST </h2>
              {error && <Card className='error_message'>{error}</Card>}
              {loading && <LoadingSpinner asOverlay />}
              <Input
                element='input'
                id='title'
                type='text'
                label='Post Title'
                placeholder='Enter Title'
                validators={[VALIDATOR_REQUIRE()]}
                errorText='Name Required'
                onInput={inputHandler}
                initialValue={title}
                initialValid={true}
              />
              <Input
                element='input'
                id='email'
                type='email'
                label='E-Mail'
                placeholder='Enter E-Mail'
                validators={[VALIDATOR_EMAIL()]}
                errorText='Please enter a valid email address.'
                onInput={inputHandler}
                initialValue={email}
                initialValid={true}
              />

              <Button type='submit' disabled={!formState.isValid}>
                Register
              </Button>
            </div>
          </div>
        </div>
      </form>
      <Notification notify={notify} setNotify={setNotify} />
    </div>
  );
}

export default withRouter(ApplyScreen);
