        <React.Fragment>
        <div className="user__list">
        <h2>Users List</h2>

        {loading ?<LoadingSpinner asOverlay/>:error?<Card>{error}</Card>:
        <table>
        <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Role</th>
            <th>Candidate</th>
            <th>Actions</th>
        </tr>
        {users.map((user)=>(
            <tr key={user.id}>
            <td>{user.name}</td>
            <td>{user.email}</td>
            <td>{user.location}</td>
