import React, { useEffect, useState } from 'react';
import Button from '../shared/components/FormElements/Button';
import { useSelector, useDispatch } from 'react-redux';
import {Link} from 'react-router-dom'
import Card from '../shared/components/UIElements/Card';
import LoadingSpinner from '../shared/components/UIElements/LoadingSpinner';
import { USER_UPDATE_RESET } from '../constants/UserConstant';
import './EditProfileScreen.css'
import Notification from '../shared/components/UIElements/Notification'

import {updateUser } from '../Action/UserActions';

function CreatePost({ match, history }) {
  const [notify, setNotify] = useState({
    isOpen: false,
    message: '',
    type: '',
  });
 const userId = match.params.id
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [isActive, setIsActive] = useState(false);
  const [isApplying, setIsApplying] = useState(false);


  const dispatch = useDispatch()

  const useDetails = useSelector(state => state.useDetails)
  const {loading,error,user} = useDetails


  const userUpdate = useSelector(state => state.userUpdate)
  const {success:successUpdate,error:errorUpdate,loading:loadingUpdate} = userUpdate

  useEffect(() => {
    if (successUpdate) {
      dispatch({type:USER_UPDATE_RESET})
      history.push('/admin/users')

    }else{
    //   
    }


  }, [dispatch,user,userId,history,successUpdate])

  const updateSubmitHandler = (e) => {
    e.preventDefault();

   dispatch(updateUser({
    //  id:user.id, name,email,location,bio,isAdmin,isCandidate
   }))
    setNotify({
      isOpen: true,
      message: 'Submitted Successfully',
      type: 'success',
    });

  };
  return (

    <div>
    <Link to="/admin/posts/">
    <button  style={{position:'absolute',left:'200px' ,outline:'none'}} type="button" className="btn btn-outline-secondary">GO BACK</button>
    </Link>
    <form onSubmit={updateSubmitHandler}>
      <div className='login-form-register'>
        <div className='container-form'>
        {errorUpdate&&<Card>{errorUpdate}</Card>}
        {loadingUpdate&&<LoadingSpinner asOverlay/>}
        {loading? <LoadingSpinner/>:error?<Card>{error}</Card>:
        <div className='form-all'>
        <h2>EDIT USER  </h2>

        <div className="form-control">
      <label htmlFor="title">Post Title</label>
      <input 
      type="text" 
      id="title" 
      placeholder="Enter Post Title" 
      value={title}
      onChange={(e) => setTitle(e.target.value)} />

    </div>
    <div className="form-control">
      <label htmlFor="description">Description</label>
      <textarea type="text" 
      id="description" 
      placeholder="Write a Description"  
      value={description}
      onChange={(e) => setDescription(e.target.value)}/>
    </div>
    <div className="form-control">
    <div className="form-check form-switch">
      <input checked={isActive} onChange={(e) => setIsActive(e.target.checked)} className="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
      <label className="form-check-label" for="flexSwitchCheckDefault">Active</label>
    </div>

  </div>
    <div className="form-control">
    <div className="form-check form-switch">
    <input checked={isApplying}  onChange={(e) => setIsApplying(e.target.checked)} className="form-check-input" type="checkbox" id="flexSwitchCheckDefault"/>
    <label className="form-check-label" for="flexSwitchCheckDefault">Users Application </label>

    </div>
    </div>

        <Button type='submit'>
          SAVE UPDATES
        </Button>




</div>
      }

        </div>
      </div>
    </form>
    <Notification notify={notify} setNotify={setNotify} />

    </div>

  );
}



export default CreatePost;
