import React, { useEffect } from 'react';
import { Grid } from '@material-ui/core';
import { Useform, Form } from './Useform';
import Controls from '../../controls/Controls'

const GenderItem = [
  { id: 'male', title: 'male' },
  { id: 'female', title: 'Female' },
  { id: 'other', title: 'Other' },
];
const initialValues = {
  id: 0,
  fullName: '',
  email: '',
  mobile: '',
  city: '',
  gender: 'male',
  departmentId: '',
  hireDate: new Date(),
  isPermanent: false,
};

const EmployeesForm = (props) => {
  const { addOrEdit, recordForEdit } = props;
  const validate = (fieldsValues = values) => {
    let temp = { ...errors };
    if ('fullName' in fieldsValues)
      temp.fullName = fieldsValues.fullName ? '' : 'This field is required.';
    if ('email' in fieldsValues)
      temp.email = /$^|.+@.+..+/.test(fieldsValues.email)
        ? ''
        : 'Email is not field';
    if ('mobile' in fieldsValues)
      temp.mobile =
        fieldsValues.mobile.length > 9 ? '' : 'Minimum 10 numbers required';
    if ('departmentId' in fieldsValues)
      temp.departmentId =
        fieldsValues.departmentId.length !== 0 ? '' : 'This field is required';
    setErrors({
      ...temp,
    });
    if (fieldsValues === values)
      return Object.values(temp).every((x) => x === '');
  };
  const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetForm,
  } = Useform(initialValues, true, validate);
  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate()) {
      addOrEdit(values, resetForm);
    }
  };
  useEffect(() => {
    if (recordForEdit !== null) {
      setValues({
        ...recordForEdit,
      });
    }
  }, [recordForEdit, setValues]);
  return (
    <Form onSubmit={handleSubmit}>
      <Grid container>
        <Grid item xs={6}>
          <Controls.Input
            name='fullName'
            label='Full name'
            value={values.fullName}
            error={errors.fullName}
            onChange={handleInputChange}
          />
          <Controls.Input
            label='Email'
            name='email'
            value={values.email}
            onChange={handleInputChange}
            error={errors.email}
          />
          <Controls.Input
            label='Mobile'
            name='mobile'
            value={values.mobile}
            onChange={handleInputChange}
            error={errors.mobile}
          />
          <Controls.Input
            label='City'
            name='city'
            value={values.city}
            onChange={handleInputChange}
          />
        </Grid>
        <Grid item xs={6}>



          <Controls.CheckBox
            name='isPermanent'
            label='permanent Employee'
            value={values.isPermanent}
            onChange={handleInputChange}
          />

          <div>
            <Controls.Button type='submit' text='Submit' />
            <Controls.Button onClick={resetForm} color='default' text='Reset' />
          </div>
        </Grid>
      </Grid>
    </Form>
  );
};

export default EmployeesForm;
