import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import UsersList from '../components/UsersList';
import ErrorModal from '../../shared/components/UIElements/ErrorModal';
import LoadingSpinner from '../../shared/components/UIElements/LoadingSpinner';
import { listPost } from '../../Action/PostActions';

const Post = () => {
  const dispatch = useDispatch();

  const postList = useSelector((state) => state.postList);
  const { error, loading, posts } = postList;

  useEffect(() => {
    dispatch(listPost());
  }, [dispatch]);

  const Posts = [
    {
      id: 1,
      title: 'Manager',
      description:
        'Duis mollis, est non commodo luctus, nisi erat porttitor ligula Integer posuere erat a ante venenatis dapibus posuere velit aliquet.',
      image: '/images/camera.jpg',
      candidates: 5,
      createdAt: '2021-03-20',
      user: 1,
    },
    {
      id: 2,
      title: 'HR',
      description:
        'Duis mollis, est non commodo luctus, nisi erat porttitor ligula Integer posuere erat a ante venenatis dapibus posuere velit aliquet.',
      image: '/images/alexa.jpg',
      candidates: 3,
      createdAt: '2021-03-20',
      user: 1,
    },
  ];
  return (
    <React.Fragment>
      <ErrorModal error={error} />
      {loading && (
        <div className='center'>
          <LoadingSpinner asOverlay />
        </div>
      )}
      {<UsersList posts={Posts} />}
    </React.Fragment>
  );
};

export default Post;
